const { DataTypes } = require('sequelize');
const sequelize = require('../config/db.config');
  const AccountingType = sequelize.define('AccountingTypes', {
      AccountingTypeID: {
          type: DataTypes.UUID,
          defaultValue: DataTypes.UUIDV4,
          primaryKey: true,
          allowNull: false,
      },
      AccountingTypeName: {
          type: DataTypes.STRING,
          allowNull: false,

      },
      AccountingTypeCode: {
          type: DataTypes.STRING,
          allowNull: false,

      },
      Status: {
          type: DataTypes.STRING,
          allowNull: false,

      },
  }, {
      modelName: 'AccountingType'
  })

  module.exports = AccountingType;